import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class NameForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: '', location: "Los Angeles, US"};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
      this.handleChange = this.handleChangeLocation.bind(this);
    }

    handleChange(event) {
      this.setState({
          value: event.target.value,
          location: this.state.location,
        });
    }
    handleChangeLocation(event) {
        this.setState({
            value: this.state.value,
            location: event.target.value,
        })
    }

    handleSubmit(event) {
      this.openInNewTab(this.state.value)
      event.preventDefault();
    }

    openInNewTab(url) {

        const win = window.open(url, '_blank');
        if (win != null) {
          win.focus();
        }

      }

    render() {
      return (
        <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Enter a link to view
            <input type="text" value={this.state.value} onChange={this.handleChange} placeholder="Search the world..." />
          </label>
          <input type="submit" value="Submit" />
          <button onclick="myFunction()"></button>
        </form>
        <select 
            value={this.state.location} 
            onChange={this.handleChangeLocation} 
            >
            <option value="Los Angeles, US">Los Angeles, US</option>
            <option value="Perth, AU">Perth, AU</option>

        </select>
        </div>
      );
    }
  }

  ReactDOM.render(
    <NameForm />,
    document.getElementById('root')
  );
