import React from 'react';
import './Home.css';


class Home extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: '', location: 'Los Angeles, USA', isFavorite: false, showImage: false};
      this.handleChange = this.handleChange.bind(this);
      this.displayImage = this.displayImage.bing(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const target = event.target;
        const name = target.name;
        const value = name == 'link' ? target.value : 
        name == "isFavorite" ? target.isFavorite : target.location;

        this.setState({
            [name]: value
        });    
    }

    displayImage(){
        this.setState({
            value: this.state.value,
            location: this.state.location,
            isFavorite: this.state.isFavorite,
            showImage: true,
        });
    }

    handleSubmit(event) {
      this.openInNewTab(this.state.value)
      event.preventDefault();
    }

    openInNewTab(url) {

        const win = window.open(url, '_blank');
        if (win != null) {
          win.focus();
        }

    }

    render() {
      return (
            <div> 
                <h1> 
                    Enter a link and location to view
                </h1>
                <form>
                    <input
                    name="value" 
                    type="text" 
                    value={this.state.value} 
                    onChange={this.handleChange} 
                    placeholder="Search the world..." />


                <button onClick={this.displayImage}>
                    Submit
                </button>
                <br />

                    <select
                    name="location" 
                    location={this.state.location} 
                    onChange={this.handleChange} 
                    >
                    <option location="Los Angeles, USA">Los Angeles, USA</option>
                    <option location="Perth, AU">Perth, AU</option>
                    </select>

                <br />
                <label class="favorite">
                    Favorite
                    <input
                        name="isFavorite"
                        type="checkbox"
                        checked={this.state.isFavorite}
                        onChange={this.handleChange} />
                </label>
                </form>
        </div>
      );
    }
  }

export default Home;
