import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class NameForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: '', location: 'LA'};
      this.handleChange = this.handleChange.bind(this);
      this.handleLocChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value, location: this.state.location});
    }

    handleLocChange(event) {
        this.setState({value: this.state.value, location: event.target.location});
      }

    handleSubmit(event) {
      this.openInNewTab(this.state.value)
      event.preventDefault();
    }

    openInNewTab(url) {

        const win = window.open(url, '_blank');
        if (win != null) {
          win.focus();
        }
    }

    render() {
      return (
        <div>
            <div>
                <form onSubmit={this.handleSubmit}>
                <label>
                    Enter a link to view
                    <input type="text" value={this.state.value} onChange={this.handleChange} placeholder="Search the world..." />
                </label>
                <input type="submit" value="Submit" />
                <button onclick="myFunction()"></button>
                </form>
            </div>
            <div >
                <select 
                    location={this.state.location} 
                    onChange={this.handleLocChange} 
                    >
                    <option location="LA">LA</option>
                    <option location="Perth">Perth</option>
                </select>

            </div>
        </div>
      );
    }
  }

  ReactDOM.render(
    <NameForm />,
    document.getElementById('root')
  );
