import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class NameForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: '', location: 'Los Angeles, USA', isFavorite: false};
      this.handleChange = this.handleChange.bind(this);

      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const target = event.target;
        const value = target.name == 'link' ? target.value : 
        target.name == "isFavorite" ? target.isFavorite : target.location;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }



    handleSubmit(event) {
      this.openInNewTab(this.state.value)
      event.preventDefault();
    }

    openInNewTab(url) {

        const win = window.open(url, '_blank');
        if (win != null) {
          win.focus();
        }
    }

    render() {
      return (
            <div> class="Homepage"
                <h1 class="centered"> 
                    Enter a link and location to view
                </h1>
                <form >
                <label>
                    Enter a link to view
                    <input
                    onSubmit={this.handleSubmit}
                    name="link" 
                    type="text" 
                    value={this.state.value} 
                    onChange={this.handleChange} 
                    placeholder="Search the world..." />
                </label>
                <input type="submit" value="Submit" />
                <button onclick="myFunction()"></button>
                <br />
                <label>
                    Location:
                    <select
                    name="location" 
                    location={this.state.location} 
                    onChange={this.handleChange} 
                    >
                    <option location="Los Angeles, USA">Los Angeles, USA</option>
                    <option location="Perth, AU">Perth, AU</option>
                    </select>
                </label>
                <br />
                <label>
                    Favorite
                    <input
                        name="isFavorite"
                        type="checkbox"
                        checked={this.state.isFavorite}
                        onChange={this.handleChange} />
                </label>
                </form>
        </div>
      );
    }
  }

  ReactDOM.render(
    <NameForm />,
    document.getElementById('root')
  );
